using namespace std;

class Solution1 {
public:
	int diagonalSum(vector<vector<int>>& mat) {
		int result = 0;
		for (int row = 0; row < mat.size(); ++row) {
			result += mat[row][row];
		}
		for (int row = 0; row < mat.size(); ++row) {
			result += mat[row][mat.size() - 1 - row];
		}
		if (mat.size() % 2) {
			result -= mat[mat.size() / 2][mat.size() / 2];
		}
		return result;
	}

};

class Solution2 {
public:
	int numWays(string s) {
		std::vector<long int> dp(s.size(), 0);
		dp[0] = s[0] == '1' ? 1 : 0;
		for (long idx = 1; idx < s.size(); ++idx) {
			if (s[idx] == '0') {
				dp[idx] = dp[idx - 1];
			}
			else {
				dp[idx] = dp[idx - 1] + 1;
			}
		}
		long int mod = 1e9 + 7;
		long int result = 0;
		if (dp.back() == 0) {
			result = (s.size() - 1)*(s.size() - 2) / 2;
			
		}
		else {
			if (dp.back() % 3) {
				return 0;
			}
			else {
				long int first_num = 0, second_num = 0;
				long first_size = dp.back() / 3;
				long second_size = first_size * 2;
				for (long idx = 0; idx < dp.size(); ++idx) {
					if (dp[idx] == first_size) {
						++first_num;
					}
					else if(dp[idx]==second_size) {
						++second_num;
					}
				}
				result = (first_num*second_num) % mod;
			}
		}
		return result;
	}
};


class Solution {
	int result = std::numeric_limits<int>::max();
public:
	int findLengthOfShortestSubarray(vector<int>& arr) {
		left2right(arr);
		right2left(arr);
		return result;
	}
private:
	void left2right(std::vector<int>& arr) {
		int left_peak_idx = -1;
		for (int idx = 1; idx < arr.size(); ++idx) {
			if (arr[idx] < arr[idx - 1]) {
				left_peak_idx = idx - 1;
				break;
			}
		}
		if (left_peak_idx == -1) {
			result = 0;
		}
		else {
			if (arr.back() >= arr[left_peak_idx]) {
				int first_eg_idx = -1;
				for (int idx = arr.size() - 2; idx >= 0; --idx) {
					if (arr[idx] <= arr[idx + 1]) {
						if (arr[idx] < arr[left_peak_idx]) {
							//if(arr[idx])
							first_eg_idx = idx + 1;
							break;
						}
					}
					else {
						first_eg_idx = idx+1;
						break;
					}
				}
				result = first_eg_idx - left_peak_idx - 1;
			}
			else {
				result = arr.size() - 1 - left_peak_idx;
			}
		}

	}
	void right2left(std::vector<int> & arr) {
		int right_lower_idx = -1;
		for (int idx = arr.size() - 2; idx >= 0; --idx) {
			if (arr[idx] > arr[idx + 1]) {
				right_lower_idx = idx + 1;
				break;
			}
		}
		if (right_lower_idx == -1) {
			result = 0;
		}
		else {
			if (arr.front() <= arr[right_lower_idx]) {
				int last_el_idx = -1;
				for (int idx = 1; idx <arr.size(); ++idx) {
					if (arr[idx] >= arr[idx - 1]) {
						if (arr[idx] > arr[right_lower_idx]) {
							last_el_idx = idx - 1;
							break;
						}
					}
					else {
						last_el_idx = idx - 1;
						break;
					}
				}
				result = std::min(result, right_lower_idx - last_el_idx - 1);
			}
			else {
				result = std::min(result, right_lower_idx);
			}
		}
	}
};

